import React from 'react'
import ReactDOM from 'react-dom'

const App = () => {
    const course = 'Half Stack application development'
    const parts = [
        {
            name: 'Fundamentals of React',
            exercises: 10
        },
        {
            name: 'Using props to pass data',
            exercises: 7
        },
        {
            name: 'State of a component',
            exercises: 14
        }
    ]

    return (
        <div>
            <Header course={course}/>
            <Content parts={parts}/>
            <Total total={props.parts[0].exercises + props.parts[1].exercises + props.parts[2].exercises}/>
        </div>
    )
}

const Header = (props) => {
    return (
        <h1>{props.course}</h1>
    )
}

const Content = (props) => {
    return (
        <div>
            <Part name={props.parts[0].name} amount={props.parts[0].exercises}/>
            <Part name={props.parts[1].name} amount={props.parts[1].exercises}/>
            <Part name={props.parts[2].name} amount={props.parts[2].exercises}/>
        </div>
    )


}

const Part = (props) => {
    return (
        <p>
            {props.name} {props.amount}
        </p>
    )
}

const Total = (props) => {
    return (
        <p>Number of exercises {props.total}</p>
    )
}

ReactDOM.render(<App/>, document.getElementById('root'))
